import React, { useState, useEffect } from 'react';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';

function Task({ userId }) {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await fetch(`http://localhost:3001/users/${userId}/stasks`);
        if (response.ok) {
          const data = await response.json();
          setTasks(data.stasks);
        } else {
          console.error('Failed to fetch tasks');
        }
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    fetchTasks();
  }, [userId]);

  const handleDelete = async (taskId) => {
    try {
      await fetch(`http://localhost:3001/users/${userId}/stasks/${taskId}`, {
        method: 'DELETE',
      });
      const updatedTasks = tasks.filter(task => task._id !== taskId);
      setTasks(updatedTasks);
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  return (
    <div>
      <h3>Tasks</h3>
      <Table striped bordered hover>
        <thead>
          {/* Table header with appropriate columns */}
        </thead>
        <tbody>
          {tasks.map((task, index) => (
            <tr key={index}>
              {/* Table rows to display task details */}
              <td>
                <Button variant="danger" onClick={() => handleDelete(task._id)}>
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default Task;
