import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom"; // Note: Import 'Outlet' for nested routing
import NavBar from "./component/NavBar";
import './index.css'
import './App.css'
import SideBar from "./component/SideBar";
import MainBar from "./component/MainBar";
import Login from "./component/Login";
import Register from "./component/Register";
import ForgetPs from "./component/ForgetPs";
import MyChart from "./component/MyChart";
import BarChart from "./component/BarChart";
import Dashboard from "./component/Dashboard";
import Task from "./component/Task";
import Class from "./component/Class";
import Syllubus from "./component/Syllubus";

function App() {
    const [isSidebarOpen, setIsSidebarOpen] = useState(false);

    const toggleSidebar = () => {
        setIsSidebarOpen(!isSidebarOpen);
    };

    useEffect(() => {
        // Always add 'sb-nav-fixed' class to the body and conditionally add/remove 'sb-sidenav-toggled' class
        document.body.classList.toggle('sb-nav-fixed', true);
        document.body.classList.toggle('sb-sidenav-toggled', isSidebarOpen);
    }, [isSidebarOpen]);

    // Function to determine whether to show the NavBar based on the current route
    const shouldShowNavBar = () => {
        return !window.location.pathname.includes('/login') &&
               !window.location.pathname.includes('/register') &&
               !window.location.pathname.includes('/forgetps');
    };

    return (
        <Router >
         {shouldShowNavBar() && (
                    <nav>
                        <NavBar toggleSidebar={toggleSidebar} />
                    </nav>
                )}
            <div id="layoutSidenav" >




                <div id="layoutSidenav_nav">
                <Routes>
                    <Route path="/" element={<SideBar />} />
                    </Routes>

                </div>
                <Routes>
                    <Route path="/" element={<MainBar />} />
                </Routes> 



                    <Routes>
                    <Route path="/dashboard" element={<Dashboard />} />
                    {/* <Route
                        path="/mychart"
                        element={
                            <Outlet>
                                <MyChart />
                            </Outlet>
                        }
                    /> */}
                    <Route path="/barchart" element={<BarChart />} />
                    <Route path="/mychart" element={<MyChart />} />
                    <Route path="/task" element={<Task />} />
                    <Route path="/class" element={<Class />} />
                    <Route path="/syllubus" element={<Syllubus />} />
                </Routes>

            </div>
            <Routes>
                <Route path="/login" element={<Login />} />
                <Route path="/register" element={<Register />} />
                <Route path="/forgetps" element={<ForgetPs />} />
            </Routes>
        </Router>
    )
}

export default App;
