import React, { useState } from 'react';

function DTask() {
  const [inputValues, setInputValues] = useState({
    sNo: '',
    batch: '',
    task: '',
    submissionDate:new Date().toISOString().slice(0, 10), 
  });

  const [tasks, setTasks] = useState([]);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setInputValues({
      ...inputValues,
      [name]: value,

    });
  
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Create a new task object and add it to the tasks array
    const newTask = { ...inputValues, id: Date.now() };
    setTasks([...tasks, newTask]);

    // Clear the input fields after submission
    setInputValues({
      sNo: '',
      batch: '',
      task: '',
      submissionDate: '',
    });
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          S.no
          <input
            type="text"
            name="sNo"
            value={inputValues.sNo}
            onChange={handleChange}
          />
        </label>
        <label>
          Batch
          <input
            type="text"
            name="batch"
            value={inputValues.batch}
            onChange={handleChange}
          />
        </label>
        <label>
          Task:
          <input
            type="text"
            name="task"
            value={inputValues.task}
            onChange={handleChange}
          />
        </label>
        <label>
          Submission Date
          <input
            type="text"
            name="submissionDate"
            value={inputValues.submissionDate}
            onChange={handleChange}
          />
        </label>
        <button type="submit">Submit Task</button>
      </form>
      <div>&nbsp;
        <h2>Task List</h2>
        <div className="card-body">
          <div className="table-responsive">
            <table className="table table-bordered" id="datatablesSimple">
              <thead>
                <tr>
                  <th>S.no</th>
                  <th>Batch</th>
                  <th>Task</th>
                  <th>Submission Date</th>
                </tr>
              </thead>
              <tbody>
                {tasks.map((task) => (
                  <tr key={task.id}>
                    <td>{task.sNo}</td>
                    <td>{task.batch}</td>
                    <td>{task.task}</td>
                    <td>{task.submissionDate}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}

export default DTask;
