import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from './AuthContext';

const UserTasks = () => {
  const { state } = useAuth();
  const { user } = state; // Assuming user object includes the user's ID

  const [userTasks, setUserTasks] = useState([]);

  useEffect(() => {
    const fetchUserTasks = async () => {
      try {
        const response = await axios.get(`http://localhost:3001/users/${user._id}/stasks`);
        setUserTasks(response.data.stasks); // Assuming the tasks are returned as an array under 'stasks'
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    if (user) {
      fetchUserTasks();
    }
  }, [user]);

  return (
    <div>
      <h2>User Tasks</h2>
      <ul>
        {userTasks.map((task) => (
          <li key={task._id}>
            <p>Task Name: {task.taskName}</p>
            <p>Description: {task.description}</p>
            {/* Add other task details you want to display */}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default UserTasks;
