import { useState } from 'react';

const useContent = () => {
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    const handleLogin = async (email, password) => {
        try {
            const response = await fetch("http://localhost:3001/signin", {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email, password }),
            });

            if (response.ok) {
                setIsAuthenticated(true); // Set authentication status on successful login
            } else {
                setIsAuthenticated(false); // Unset authentication status on login failure
                // You might want to show an error message here
            }
        } catch (error) {
            console.error("Error:", error);
        }
    };

    const handleLogout = () => {
        setIsAuthenticated(false); // Reset authentication status on logout
    };

    return { isAuthenticated, handleLogin, handleLogout };
};

export default useContent;
