//import React, { useState, useEffect } from 'react';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import DoughnutChart from '../Dashboard/DoughnutChart';
import axios from 'axios';

function Task({ Dtasks,tasks, setTasks }) {
  
 
  const handleDelete = async (taskId) => {
    try {
        const response = await axios.delete(`http://localhost:3001/deleteTask/${taskId}`);
        if (response.status === 200) {
            const updatedTasks = tasks.filter(task => task._id !== taskId);
            setTasks(updatedTasks);
        } else {
            console.error('Task deletion failed');
        }
    } catch (error) {
        console.error('Error deleting task:', error);
    }
};



  return (
    <div>
      <h3>Tasks</h3>
      <DoughnutChart tasks={Dtasks} />
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>S.no</th>
            <th>Task Name</th>
            <th>Description</th>
            <th>Status</th>
            <th>Link</th>
            <th>Completed On</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
        {tasks &&
            tasks.map((task, index) => (
              <tr key={task._id}>
                <td>{task.taskName}</td>
                <td>{task.description}</td>
                <td>{task.status}</td>
                <td>{task.link}</td>
                <td>{task.completedOn}</td>
                <td>
                  <Button variant="danger" onClick={() => handleDelete(task._id)}>
                    Delete
                  </Button>
                </td>
              </tr>
            ))}
        </tbody>
      </Table>
    </div>
  );
}

export default Task;
