import React, { useState } from 'react';
import Btask from './Btask'; // Import the TaskData from Btask.js

function DTask() {
  const [inputValues, setInputValues] = useState({
    sNo: '',
    batch: '',
    task: '',
    submissionDate: new Date().toISOString().slice(0, 10),
  });

  // Initialize the tasks state with the data from Btask.js
  const [tasks, setTasks] = useState(Btask);

  const [editingTask, setEditingTask] = useState(null);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setInputValues({
      ...inputValues,
      [name]: value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Create a new task object and add it to the tasks array
    if (editingTask) {
      const updatedTasks = tasks.map((task) =>
        task.sNo === editingTask.sNo ? inputValues : task
      );
      setTasks(updatedTasks);
      setEditingTask(null);
    } else {
      const newTask = { ...inputValues };
      setTasks([...tasks, newTask]);
    }

    // Clear the input fields after submission
    setInputValues({
      sNo: '',
      batch: '',
      task: '',
      submissionDate: new Date().toISOString().slice(0, 10),
    });
  };

  const handleEdit = (task) => {
    setInputValues(task);
    setEditingTask(task);
  };

  const handleDelete = (task) => {
    const updatedTasks = tasks.filter((t) => t.sNo !== task.sNo);
    setTasks(updatedTasks);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="sNo" className="form-label">
            S.no
          </label>
          <input
            type="text"
            className="form-control"
            name="sNo"
            value={inputValues.sNo}
            onChange={handleChange}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="batch" className="form-label">
            Batch
          </label>
          <input
            type="text"
            className="form-control"
            name="batch"
            value={inputValues.batch}
            onChange={handleChange}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="task" className="form-label">
            Task:
          </label>
          <input
            type="text"
            className="form-control"
            name="task"
            value={inputValues.task}
            onChange={handleChange}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="submissionDate" className="form-label">
            Submission Date
          </label>
          <input
            type="date"
            className="form-control"
            name="submissionDate"
            value={inputValues.submissionDate}
            onChange={handleChange}
          />
        </div>
        <button type="submit" className="btn btn-primary">
          {editingTask ? 'Update Task' : 'Submit Task'}
        </button>
      </form>
      <div>&nbsp;</div>
      <h2>Task List</h2>
      <div className="card">
        <div className="card-body">
          <div className="table-responsive">
            <table className="table table-bordered" id="datatablesSimple">
              <thead>
                <tr>
                  <th>S.no</th>
                  <th>Batch</th>
                  <th>Task</th>
                  <th>Submission Date</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {tasks.map((task) => (
                  <tr key={task.sNo}>
                    <td>{task.sNo}</td>
                    <td>{task.batch}</td>
                    <td>{task.task}</td>
                    <td>{task.submissionDate}</td>
                    <td>
                      <button
                        onClick={() => handleEdit(task)}
                        className="btn btn-sm btn-primary"
                      >
                        Edit
                      </button>
                      <button
                        onClick={() => handleDelete(task)}
                        className="btn btn-sm btn-danger"
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}

export default DTask;
