import React, { useState } from 'react';
import axios from 'axios';

const TaskSubmission = () => {
  const [newTask, setNewTask] = useState({
    taskName: '',
    description: '',
    status: '',
    completedOn: '',
    link: '',
  });

  const handleTaskSubmission = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:3001/submitTask', newTask);
      if (response.status === 200) {
        console.log('Task submitted:', response.data.task);
        // Add the submitted task to the UI if needed
      } else {
        console.error('Task submission failed');
      }
    } catch (error) {
      console.error('Error submitting task:', error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewTask({ ...newTask, [name]: value });
  };

  return (
    <div>
      <h2>Task Submission</h2>
      <form onSubmit={handleTaskSubmission}>
        <input
          type="text"
          name="taskName"
          value={newTask.taskName}
          onChange={handleChange}
          placeholder="Task Name"
        />
        <input
          type="text"
          name="description"
          value={newTask.description}
          onChange={handleChange}
          placeholder="Description"
        />
        <input
          type="text"
          name="status"
          value={newTask.status}
          onChange={handleChange}
          placeholder="Status"
        />
        <input
          type="text"
          name="completedOn"
          value={newTask.completedOn}
          onChange={handleChange}
          placeholder="Completed On"
        />
        <input
          type="text"
          name="link"
          value={newTask.link}
          onChange={handleChange}
          placeholder="Task Link"
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default TaskSubmission;
