import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import NavBar from "./component/NavBar";
import Sidebar from "./component/Sidebar/Sidebar";
import Login from "./component/Login";
import Logout from "./component/Logout";
import Register from "./component/Register";
import ForgetPs from "./component/ForgetPs";
import Dashboard from "./component/Dashboard/Dashboard";
import DoughnutChart from "./component/Dashboard/DoughnutChart";
import Dclass from "./component/Sidebar/Dclass";
import Syllubus from "./component/Sidebar/Syllubus";
import Task from "./component/Sidebar/Task";
import TasksSubmission from "./component/Sidebar/TasksSubmission";
import useContent from './useContent'; // Imported the useContent hook

const App = () => {
    const { isAuthenticated, handleLogin, handleLogout } = useContent(); // UsING the useContent hook
    const [userEmail, setUserEmail] = useState(''); // State to hold the user's email

       // This function is used to set the user's email after login
       const handleLoginWithEmail = async (email, password) => {
        try {
            await handleLogin(email, password); // Assuming handleLogin is a promise-based function

            // Set the user's email after successful login
            if (isAuthenticated) {
                setUserEmail(email);
            }
        } catch (error) {
            console.error("Error:", error);
        }
    };


    const [isSidebarOpen, setIsSidebarOpen] = useState(false);

    const toggleSidebar = () => {
        setIsSidebarOpen(!isSidebarOpen);
    };

    useEffect(() => {
        document.body.classList.toggle('sb-nav-fixed', true);
    }, []);

    useEffect(() => {
        document.body.classList.toggle('sb-sidenav-toggled', isSidebarOpen);
    }, [isSidebarOpen]);

    return (
        <Router>
            <div id="layoutSidenav">
                {isAuthenticated && (
                    <nav>
                        <NavBar toggleSidebar={toggleSidebar} handleLogout={handleLogout} />
                    </nav>
                )}
                <div id="layoutSidenav_nav">
                    {isAuthenticated && <Sidebar />}
                </div>
                <div id="layoutSidenav_content">
                    <Routes>
                    <Route 
                    path="/"
                    element={isAuthenticated ?
                        <Navigate to="/dashboard" />
                      : <Login handleLogin={handleLogin} />}
                />
                        <Route path="/login" element={<Login handleLogin={handleLogin} />} />
                        <Route path="/register" element={<Register />} />
                        <Route path="/forgetps" element={<ForgetPs />} />
                        <Route path="/logout" element={<Logout />} />

                        {isAuthenticated ? (
                            <>
                                <Route path="/dashboard" element={<Dashboard />} />
                                <Route path="/donut" element={<DoughnutChart />} />
                                <Route path="/class" element={<Dclass />} />
                                <Route path="/task" element={<Task />} />
                                <Route path="/tasksubmit" element={<TasksSubmission />} />
                                <Route path="/syllubus" element={<Syllubus />} />

                            </>
                        ) : null}

                        <Route path="*" element={<Navigate to="/404" />} />
                        <Route path="/404" element={<h1>404 / Login Again</h1>} />
                    </Routes>
                </div>
            </div>
        </Router>
    );
};

export default App;
